CREATE TABLE IF NOT EXISTS `users` (
  `id` INT PRIMARY KEY AUTO_INCREMENT,
  `name` TINYTEXT,
  `email` TEXT,
  `password` TINYTEXT,
  `creation_date` TIMESTAMP,
  `description` TEXT,
  `is_admin` BOOLEAN,
  `ban_date` DATETIME,
  `image_link` TEXT
);

CREATE TABLE IF NOT EXISTS `dvds` (
  `id` INT PRIMARY KEY AUTO_INCREMENT,
  `title` TINYTEXT,
  `description` TEXT,
  `director` TINYTEXT,
  `genre` TINYTEXT,
  `year_of_production` SMALLINT,
  `addition_date` TIMESTAMP,
  `available_amount` TINYINT,
  `image_link` TEXT
);

CREATE TABLE IF NOT EXISTS `rents` (
  `id` INT PRIMARY KEY AUTO_INCREMENT,
  `id_user` INT,
  `id_dvd` INT,
  `rent_date` DATETIME,
  `return_deadline` DATETIME,
  `return_date` DATETIME
);

